import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import RoomListing from "./components/RoomListing";
import BookingForm from "./components/BookingForm";
import BookingDetails from "./components/BookingDetails";
import "./App.css";

// Dummy Admin Component (you can replace with actual admin panel later)
const AdminPanel = () => (
  <div style={{ padding: "20px" }}>
    <h2>Admin Panel</h2>
    <p>Here the admin can manage rooms and bookings.</p>
  </div>
);

// Dummy Home Component
const Home = () => (
  <div style={{ padding: "20px" }}>
    <h2>Welcome to Hotel Booking System üè®</h2>
    <p>Select a menu item to continue.</p>
  </div>
);

function App() {
  return (
    <Router>
      <Navbar />
      <div className="container">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/rooms" element={<RoomListing />} />
          <Route path="/booking/:roomId" element={<BookingForm />} />
          <Route path="/bookings" element={<BookingDetails />} />
          <Route path="/admin" element={<AdminPane />} />
          <Route path="/mybookings" element={<MyBookings />} />
<Route path="/bookings" element={<BookingDetails />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
