import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { createBooking } from "../utils/api"; // Make sure this points to your API helper
import "./BookingForm.css";

const BookingForm = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    checkIn: "",
    checkOut: "",
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: "" }); // Clear error on change
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = "Name is required";
    if (!formData.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!/^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(formData.email)) {
      newErrors.email = "Invalid email format";
    }
    if (!formData.checkIn) newErrors.checkIn = "Check-in date is required";
    if (!formData.checkOut) newErrors.checkOut = "Check-out date is required";
    if (formData.checkIn && formData.checkOut && formData.checkOut <= formData.checkIn) {
      newErrors.checkOut = "Check-out date must be after check-in";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    const bookingPayload = {
      guestName: formData.name,
      guestEmail: formData.email,
      checkInDate: formData.checkIn,
      checkOutDate: formData.checkOut,
      roomId: parseInt(roomId, 10), // Matches backend BookingRequest
    };

    try {
      await createBooking(bookingPayload);
      alert("Booking successful!");
      navigate("/bookings"); // Redirect to bookings page
    } catch (error) {
      console.error("Booking failed:", error.response?.data || error.message);
      alert(
        "Booking failed: " +
          JSON.stringify(error.response?.data || "Internal Server Error")
      );
    }
  };

  return (
    <form className="booking-form" onSubmit={handleSubmit}>
      <h2>Book Your Room</h2>

      <div className="form-group">
        <label>Name:</label>
        <input
          name="name"
          value={formData.name}
          onChange={handleChange}
        />
        {errors.name && <span className="error">{errors.name}</span>}
      </div>

      <div className="form-group">
        <label>Email:</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
        />
        {errors.email && <span className="error">{errors.email}</span>}
      </div>

      <div className="form-group">
        <label>Check-In:</label>
        <input
          type="date"
          name="checkIn"
          value={formData.checkIn}
          onChange={handleChange}
        />
        {errors.checkIn && <span className="error">{errors.checkIn}</span>}
      </div>

      <div className="form-group">
        <label>Check-Out:</label>
        <input
          type="date"
          name="checkOut"
          value={formData.checkOut}
          onChange={handleChange}
        />
        {errors.checkOut && <span className="error">{errors.checkOut}</span>}
      </div>

      <button type="submit" className="submit-btn">
        Confirm Booking
      </button>
    </form>
  );
};

export default BookingForm;
