import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Room API calls
export const getRooms = () => api.get('/rooms').then(response => response);
export const getAvailableRooms = () => api.get('/rooms/available').then(response => response);

// Booking API calls
export const getBookings = () => api.get('/bookings').then(response => response);
export const createBooking = (bookingData) => api.post('/bookings', bookingData).then(response => response);
export const updateBookingStatus = (bookingId, status) => 
  api.put(`/bookings/${bookingId}/status`, { status }).then(response => response);

export default api;
