import React, { useEffect, useState } from "react";
import { getBookings } from "../utils/api";
import "./MyBookings.css";

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const res = await getBookings();
        console.log("Full API Response:", res); // <-- log the entire response

        // Check what your API actually returns:
        // if it's an array directly: setBookings(res || []);
        // if it's { data: [...] }: setBookings(res.data || []);
        setBookings(Array.isArray(res) ? res : res?.data || []);
      } catch (err) {
        console.error("Error fetching bookings:", err);
        setBookings([]);
      } finally {
        setLoading(false);
      }
    };

    fetchBookings();
  }, []);

  if (loading) return <p style={{ textAlign: "center" }}>Loading your bookings...</p>;

  return (
    <div className="booking-list">
      <h2>My Bookings</h2>

      {bookings.length === 0 ? (
        <div className="empty-state">You have no bookings yet</div>
      ) : (
        <table className="bookings-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Room Name</th>
              <th>Room Number</th>
              <th>Check-In</th>
              <th>Check-Out</th>
              <th>Total Price</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((b) => (
              <tr key={b.bookingId}>
                <td>{b.bookingId || "N/A"}</td>
                <td>{b.room?.roomName || b.room?.name || "N/A"}</td>
                <td>{b.room?.roomNumber || "N/A"}</td>
                <td>
                  {b.checkInDate
                    ? new Date(b.checkInDate).toLocaleDateString()
                    : "N/A"}
                </td>
                <td>
                  {b.checkOutDate
                    ? new Date(b.checkOutDate).toLocaleDateString()
                    : "N/A"}
                </td>
                <td>{b.totalPrice !== undefined ? `$${b.totalPrice}` : "N/A"}</td>
                <td>
                  <span
                    className="status-badge"
                    style={{
                      backgroundColor: STATUS_COLORS[b.status] || "#ccc",
                    }}
                  >
                    {STATUS_LABELS[b.status] || b.status || "N/A"}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default MyBookings;
