import axios from "axios";

const API_BASE_URL = "http://localhost:8080/api"; // Spring Boot backend

// ---- BOOKINGS ----
export const createBooking = (bookingData) => {
  return axios.post(`${API_BASE_URL}/bookings`, bookingData);
};


export const getBookings = () => axios.get(`${API_BASE}/bookings`);

export const updateBookingStatus = (id, status) =>
  axios.put(`${API_BASE}/bookings/${id}`, { status });


// ---- ROOMS ----
export const getRooms = () => {
  return axios.get(`${API_BASE_URL}/rooms`);
};

export const getAvailableRooms = () => {
  return axios.get(`${API_BASE_URL}/rooms/available`);
};

export const createRoom = (roomData) => {
  return axios.post(`${API_BASE_URL}/rooms`, roomData);
};
