import React, { useState, useEffect } from "react";
import { getBookings } from "../utils/api";
import { STATUS_COLORS, STATUS_LABELS } from "../utils/constants";
import "./BookingList.css";

const MyBookings = ({ userEmail }) => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchBookings();
  }, []);

    const fetchBookings = async () => {
      try {
      setLoading(true);
      setError("");
      const response = await getBookings(); // fetch all bookings
      const data = response?.data || response || [];

      // filter only current user's bookings
      const myBookings = data.filter(
        (b) => b.guestEmail === userEmail
      );

      setBookings(myBookings);
      } catch (err) {
      setError("Could not load your bookings. Please try again later.");
        console.error("Error fetching bookings:", err);
      } finally {
        setLoading(false);
      }
    };

  if (loading) return <div className="loading">Loading bookings...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="booking-list">
      <h2>My Bookings</h2>

      {bookings.length === 0 ? (
        <p>No bookings available.</p>
      ) : (
        bookings.map((booking, index) => (
          <div key={index} className="booking-card">
            {Object.keys(booking).map((key) => (
              <p key={key}>
                <strong>{key}:</strong>{" "}
                {typeof booking[key] === "object" && booking[key] !== null
                  ? JSON.stringify(booking[key])
                  : booking[key]?.toString() || "N/A"}
              </p>
            ))}
            <hr />
          </div>
        ))
      )}
    </div>
  );
};

export default MyBookings;
