import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { createBooking } from "../utils/api";
import "./BookingForm.css";

const BookingForm = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    checkIn: "",
    checkOut: "",
  });

  const [errors, setErrors] = useState([]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // ---- FRONTEND VALIDATION ----
    const validationErrors = [];
    if (!formData.name.trim()) validationErrors.push("Name is required");
    if (!formData.email.trim()) validationErrors.push("Email is required");
    else if (!/^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(formData.email))
      validationErrors.push("Invalid email format");

    if (!formData.checkIn) validationErrors.push("Check-in date is required");
    if (!formData.checkOut) validationErrors.push("Check-out date is required");
    if (formData.checkIn && formData.checkOut && formData.checkOut <= formData.checkIn)
      validationErrors.push("Check-out date must be after check-in date");

    if (validationErrors.length > 0) {
      setErrors(validationErrors);
      return;
    }

    setErrors([]); // Clear previous errors

    // ---- PREPARE PAYLOAD ----
    const bookingPayload = {
      guestName: formData.name.trim(),
      guestEmail: formData.email.trim(),
      checkInDate: formData.checkIn,
      checkOutDate: formData.checkOut,
      roomId: parseInt(roomId, 10), // Must match backend field
    };

    try {
      await createBooking(bookingPayload);
      alert("Booking successful!");
      navigate("/bookings");
    } catch (err) {
      console.error("Booking failed:", err);

      // Backend might return object with { timestamp, status, error, path }
      if (err && typeof err === "object") {
        setErrors([err.error || JSON.stringify(err)]);
      } else {
        setErrors([err.toString()]);
      }
    }
  };

  return (
    <form className="booking-form" onSubmit={handleSubmit}>
      <h2>Book Your Room</h2>

      {/* Display validation or backend errors */}
      {errors.length > 0 && (
        <div className="error-messages">
          <ul>
            {errors.map((err, idx) => (
              <li key={idx}>{typeof err === "string" ? err : JSON.stringify(err)}</li>
            ))}
          </ul>
        </div>
      )}

      <div className="form-group">
        <label>Name:</label>
        <input
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </div>

      <div className="form-group">
        <label>Email:</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />
      </div>

      <div className="form-group">
        <label>Check-In:</label>
        <input
          type="date"
          name="checkIn"
          value={formData.checkIn}
          onChange={handleChange}
          required
        />
      </div>

      <div className="form-group">
        <label>Check-Out:</label>
        <input
          type="date"
          name="checkOut"
          value={formData.checkOut}
          onChange={handleChange}
          required
        />
      </div>

      <button type="submit" className="submit-btn">
        Confirm Booking
      </button>
    </form>
  );
};

export default BookingForm;
