import React, { useState, useEffect } from 'react';
import { getBookings, updateBookingStatus } from '../utils/api';

const AdminPanel = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await getBookings();
      // Filter to show only pending bookings
      const pendingBookings = response.data.filter(booking => booking.status === 'PENDING');
      setBookings(pendingBookings);
    } catch (err) {
      setError('Error loading bookings. Please try again later.');
      console.error('Error fetching bookings:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleStatusUpdate = async (bookingId, status) => {
    try {
      setError('');
      setSuccessMessage('');
      await updateBookingStatus(bookingId, status);
      setSuccessMessage(`Booking ${bookingId} has been ${status.toLowerCase()}`);

      // Refresh the bookings list
      fetchBookings();
    } catch (err) {
      const errorMsg = err.response?.data?.message || 'Update failed. Please try again.';
      setError(errorMsg);
    }
  };

  if (loading) return <div className="loading">Loading bookings...</div>;



  return (
    <div className="admin-panel">
      <h2>Admin Panel - Pending Bookings</h2>

      {successMessage && <div className="success-message">{successMessage}</div>}
      {error && <div className="error-message">[Error - You need to specify the message]</div>}

      <table className="admin-table">
        <thead>
          <tr>
            <th>Booking ID</th>
            <th>Guest Name</th>
            <th>Room Number</th>
            <th>Check-in</th>
            <th>Check-out</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map(booking => (
            <tr key={booking.bookingId}>
              <td>{booking.bookingId}</td>
              <td>{booking.guestName}</td>
              <td>{booking.room?.roomNumber}</td>
              <td>{new Date(booking.checkInDate).toLocaleDateString()}</td>
              <td>{new Date(booking.checkOutDate).toLocaleDateString()}</td>
              <td>
                <button
                  className="approve-btn"
                  onClick={() => handleStatusUpdate(booking.bookingId, 'APPROVED')}
                >
                  Approve
                </button>
                <button
                  className="reject-btn"
                  onClick={() => handleStatusUpdate(booking.bookingId, 'REJECTED')}
                >
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {bookings.length === 0 && !loading && (
        <div className="empty-state">No pending bookings</div>
      )}
    </div>
  );
};

export default AdminPanel;
