import React, { useState, useEffect, useCallback } from 'react';
import { getRooms, getAvailableRooms } from '../utils/api';
import { useNavigate } from "react-router-dom";
import "./Room.css";



const RoomListing = () => {
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAvailableOnly, setShowAvailableOnly] = useState(false);
  const navigate = useNavigate();

  const fetchRooms = useCallback(async () => {
    try {
      setLoading(true);
      setError('');
      const response = showAvailableOnly 
        ? await getAvailableRooms() 
        : await getRooms();
      setRooms(response.data);
    } catch (err) {
      setError('Could not load rooms. Please try again later.');
      console.error('Error fetching rooms:', err);
    } finally {
      setLoading(false);
    }
  }, [showAvailableOnly]);

  useEffect(() => {
    fetchRooms();
  }, [fetchRooms]);

  if (loading) return <div className="loading">Loading rooms...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="room-listing">
      <h2>Available Rooms</h2>

      <div className="filter-control">
        <label>
          <input
            type="checkbox"
            checked={showAvailableOnly}
            onChange={() => setShowAvailableOnly(!showAvailableOnly)}
            aria-label="show available only"
          />
          Show available only
        </label>
      </div>

      <table className="rooms-table">
        <thead>
          <tr>
            <th>Room Number</th>
            <th>Type</th>
            <th>Capacity</th>
            <th>Price/Night</th>
            <th>Availability</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {rooms.map(room => (
            <tr key={room.roomId}>
              <td>{room.roomNumber}</td>
              <td>{room.roomType}</td>
              <td>{room.capacity}</td>
              <td>${room.pricePerNight}</td>
              <td>
                <span className={`status-badge ${room.available ? 'available' : 'unavailable'}`}>
                  {room.available ? 'Available' : 'Unavailable'}
                </span>
              </td>
              <td>
                <button
                  data-testid={`book-btn-${room.roomId}`}
                  className="book-btn"
                  disabled={!room.available}
                  onClick={() => navigate(`/book/${room.roomId}`)} // ✅ React Router navigation
                >
                  Book Now
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {rooms.length === 0 && (
        <div className="empty-state">No rooms found</div>
      )}
    </div>
  );
};

export default RoomListing;
