import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAvailableRooms } from "../utils/api"; // fetch only available rooms
import "./RoomListing.css";

const RoomListing = () => {
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRooms = async () => {
      try {
        const response = await getAvailableRooms(); // only available rooms
        console.log("Available rooms:", response);

        // Backend might wrap data in 'data', adjust if needed
        const roomData = Array.isArray(response) ? response : response?.data || [];
        setRooms(roomData);
      } catch (err) {
        console.error("Error fetching rooms:", err);
        setRooms([]);
      } finally {
        setLoading(false);
      }
    };

    fetchRooms();
  }, []);

  if (loading) return <p style={{ textAlign: "center" }}>Loading rooms...</p>;

  return (
    <div className="room-listing">
      <h2>üè® Available Rooms</h2>
      {rooms.length === 0 ? (
        <p>No rooms available at the moment.</p>
      ) : (
        <div className="rooms">
          {rooms.map((room) => (
            <div key={room.roomId || room.id} className="room-card">
              <h3>{room.roomName || room.name || "Unnamed Room"}</h3>
              <p>Type: {room.type || "N/A"}</p>
              <p>Capacity: {room.capacity || "N/A"}</p>
              <p>Price per Night: ${room.pricePerNight || room.price || "N/A"}</p>
              <p>Status: {room.available ? "Available" : "Unavailable"}</p>
              {room.available ? (
                <Link to={`/book/${room.roomId || room.id}`} className="btn-book">
                  Book Now
                </Link>
              ) : (
                <button className="btn-disabled" disabled>
                  Not Available
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default RoomListing;
