import axios from "axios";

const API_BASE = "http://localhost:8080/api"; // ✅ Define API_BASE at the top

// ---- BOOKINGS ----
export const createBooking = (bookingData) => {
  return axios.post(`${API_BASE}/bookings`, bookingData);
};



// Fetch all bookings
export const getBookings = async () => {
  const res = await axios.get(`${API_BASE}/bookings`);
  return res.data;
};

// Update booking status
export const updateBookingStatus = async (id, status) => {
  const res = await axios.put(`${API_BASE}/bookings/${id}/status`, { status });
  return res.data;
};

// ---- ROOMS ----
export const getRooms = () => {
  return axios.get(`${API_BASE}/rooms`);
};

export const getAvailableRooms = () => {
  return axios.get(`${API_BASE}/rooms/available`);
};
