import React, { useEffect, useState } from "react";
import { getAllBookings, updateBookingStatus } from "../utils/api";

const AdminPanel = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      const data = await getAllBookings();
      setBookings(data.filter(b => b.status === "PENDING"));
    };
    fetchBookings();
  }, []);

  const handleStatus = async (id, status) => {
    await updateBookingStatus(id, status);
    setBookings(bookings.filter(b => b.bookingId !== id));
  };

  return (
    <div>
      <h2>Admin Panel - Pending Bookings</h2>
      {bookings.length === 0 ? <p>No pending bookings.</p> :
        <table>
          <thead>
            <tr>
              <th>ID</th><th>Guest</th><th>Email</th><th>Room</th><th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map(b => (
              <tr key={b.bookingId}>
                <td>{b.bookingId}</td>
                <td>{b.guestName}</td>
                <td>{b.guestEmail}</td>
                <td>{b.room?.number}</td>
                <td>
                  <button onClick={()=>handleStatus(b.bookingId,"APPROVED")}>Approve</button>
                  <button onClick={()=>handleStatus(b.bookingId,"REJECTED")}>Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      }
    </div>
  );
};

export default AdminPanel;
