// App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import RoomListing from './components/RoomListing';
import BookingForm from './components/BookingForm';
import BookingList from './components/BookingList';
import AdminPanel from './components/AdminPanel';
import './App.css';
//import { BrowserRouter as Router, Routes, Route } from "react-router-dom";


function App() {
  return (
    <Router>
      <div className="App">
        <header className="app-header">
          <h1>Hotel Booking System</h1>
          <Navbar />
        </header>

        <main className="app-main">
          <Routes>
            <Route path="/" element={<RoomListing />} />
            <Route path="/my-bookings" element={<BookingList />} />
            <Route path="/admin" element={<AdminPanel />} />
            <Route path="/book/:roomId" element={<BookingFormWrapper />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

// ✅ Wrapper to fetch roomId from URL and pass to BookingForm
import { useParams } from 'react-router-dom';
const BookingFormWrapper = () => {
  const { roomId } = useParams();
  const room = { roomId }; // minimal object for form
  return <BookingForm room={room} />;
};

export default App;
