import React, { useState } from 'react';
import RoomListing from './components/RoomListing';
import BookingForm from './components/BookingForm';
import BookingList from './components/BookingList';
import AdminPanel from './components/AdminPanel';
import './App.css';

function App() {
  const [currentView, setCurrentView] = useState('rooms');
  const [selectedRoom, setSelectedRoom] = useState(null);

  // Handle room selection for booking
  const handleRoomSelect = (room) => {
    setSelectedRoom(room);
    setCurrentView('booking-form');
  };

  const renderContent = () => {
    switch (currentView) {
      case 'rooms':
        return <RoomListing onRoomSelect={handleRoomSelect} />;
      case 'bookings':
        return <BookingList />;
      case 'admin':
        return <AdminPanel />;
      case 'booking-form':
        return selectedRoom ? <BookingForm room={selectedRoom} /> : <div>Room not selected</div>;
      default:
        return <RoomListing onRoomSelect={handleRoomSelect} />;
    }
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Hotel Booking System</h1>
        <nav className="nav-menu">
          <button 
            className={currentView === 'rooms' ? 'active' : ''}
            onClick={() => setCurrentView('rooms')}
          >
            Rooms
          </button>
          <button 
            className={currentView === 'bookings' ? 'active' : ''}
            onClick={() => setCurrentView('bookings')}
          >
            Bookings
          </button>
          <button 
            className={currentView === 'admin' ? 'active' : ''}
            onClick={() => setCurrentView('admin')}
          >
            Admin
          </button>
        </nav>
      </header>

      <main className="app-main">
        {renderContent()}
      </main>
    </div>
  );
}

export default App;
