import axios from "axios";

const API_BASE_URL = "http://localhost:8080/api"; // backend Spring Boot

// ---- BOOKINGS ----
export const createBooking = (bookingData) => {
  return axios.post(`${API_BASE_URL}/bookings`, bookingData);
};

export const getBookings = () => {
  return axios.get(`${API_BASE_URL}/bookings`);
};

// ✅ NEW: Update booking status
export const updateBookingStatus = (bookingId, status) => {
  return axios.put(`${API_BASE_URL}/bookings/${bookingId}/status`, { status });
};

// ---- ROOMS ----
export const getRooms = () => {
  return axios.get(`${API_BASE_URL}/rooms`);
};

export const getAvailableRooms = () => {
  return axios.get(`${API_BASE_URL}/rooms/available`);
};
