import React, { useState } from 'react';
import { createBooking } from '../utils/api';

const BookingForm = ({ room }) => {
  const [formData, setFormData] = useState({
    guestName: '',
    guestEmail: '',
    checkInDate: '',
    checkOutDate: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const validateForm = () => {
    const newErrors = {};

    if (!formData.guestName.trim()) newErrors.guestName = 'Name is required';
    if (!formData.guestEmail.trim()) newErrors.guestEmail = 'Email is required';
    if (!formData.checkInDate) newErrors.checkInDate = 'Check-in date is required';
    if (!formData.checkOutDate) newErrors.checkOutDate = 'Check-out date is required';

    if (formData.guestEmail && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.guestEmail)) {
      newErrors.guestEmail = 'Invalid email format';
    }

    if (formData.checkInDate && formData.checkOutDate) {
      const checkIn = new Date(formData.checkInDate);
      const checkOut = new Date(formData.checkOutDate);
      if (checkOut <= checkIn) {
        newErrors.checkOutDate = 'Check-out date must be after check-in date';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    setSuccessMessage('');



    if (!validateForm()) return;

    setLoading(true);
    try {
      const bookingData = {
        roomId: room.roomId,
        guestName: formData.guestName,
        guestEmail: formData.guestEmail,
        checkInDate: formData.checkInDate,
        checkOutDate: formData.checkOutDate
      };

      await createBooking(bookingData);
      setSuccessMessage('Booking created successfully!');
      setFormData({
        guestName: '',
        guestEmail: '',
        checkInDate: '',
        checkOutDate: ''
      });
    } catch (err) {
      const errorMsg = err.response?.data?.message || 'Booking failed. Please try again.';
      setErrorMessage(errorMsg);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  return (
    <div className="booking-form">
      <h2>Book Room {room.roomNumber}</h2>

      {successMessage && <div className="success-message">{successMessage}</div>}
      {errorMessage && <div className="error-message">{errorMessage}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="guestName">Guest Name:</label>
          <input
            type="text"
            id="guestName"
            name="guestName"
            value={formData.guestName}
            onChange={handleChange}
            aria-label="guest name"
          />
          {errors.guestName && <span className="error">{errors.guestName}</span>}
        </div>


        <div className="form-group">
          <label htmlFor="guestEmail">Guest Email:</label>
          <input
            type="email"
            id="guestEmail"
            name="guestEmail"
            value={formData.guestEmail}
            onChange={handleChange}
            aria-label="guest email"
          />
          {errors.guestEmail && <span className="error">{errors.guestEmail}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="checkInDate">Check-in Date:</label>
          <input
            type="date"
            id="checkInDate"
            name="checkInDate"
            value={formData.checkInDate}
            onChange={handleChange}
            aria-label="check-in"
          />
          {errors.checkInDate && <span className="error">{errors.checkInDate}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="checkOutDate">Check-out Date:</label>
          <input
            type="date"
            id="checkOutDate"
            name="checkOutDate"
            value={formData.checkOutDate}
            onChange={handleChange}
            aria-label="check-out"
          />
          {errors.checkOutDate && <span className="error">{errors.checkOutDate}</span>}
        </div>

        <button 
          type="submit" 
          disabled={loading}
          className="submit-btn"
        >
          {loading ? 'Creating Booking...' : 'Create Booking'}
        </button>
      </form>
    </div>
  );
};

export default BookingForm;
