// src/components/RoomListing.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getRooms } from "../utils/api";
import "./RoomListing.css";

const RoomListing = () => {
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRooms = async () => {
      try {
        const response = await getRooms();
        console.log("API Response:", response); // Check structure

        // Adjust based on API response
        const roomData = Array.isArray(response)
          ? response
          : response?.data || [];
        setRooms(roomData);
      } catch (err) {
        console.error("Error fetching rooms:", err);
        setRooms([]);
      } finally {
        setLoading(false);
      }
    };

    fetchRooms();
  }, []);

  if (loading) return <p style={{ textAlign: "center" }}>Loading rooms...</p>;

  return (
    <div className="room-listing">
      <h2>üè® Available Rooms</h2>
      {rooms.length === 0 ? (
        <p>No rooms available.</p>
      ) : (
        <div className="rooms">
          {rooms.map((room) => (
            <div key={room.id || room._id} className="room-card">
              <h3>{room.name || room.roomName || "Unnamed Room"}</h3>
              <p>{room.description || "No description available."}</p>
              <p>Price: ${room.price || "N/A"}</p>
              <Link to={`/booking/${room.id || room._id}`} className="btn-book">
                Book Now
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default RoomListing;
