// src/components/MyBookings.js
import React, { useEffect, useState } from "react";
import { getBookings } from "../utils/api";
import "./MyBookings.css";

const MyBookings = () => {
  const [bookings, setBookings] = useState([]); // always start as array
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const res = await getBookings();
        console.log("API Response:", res?.data); // debug
        setBookings(res?.data || []); // fallback to empty array
      } catch (err) {
        console.error("Error fetching bookings:", err);
        setBookings([]); // fallback
      } finally {
        setLoading(false);
      }
    };

    fetchBookings();
  }, []);

  if (loading) {
    return <p style={{ textAlign: "center" }}>Loading your bookings...</p>;
  }

  return (
    <div className="container">
      <h2>ðŸ“– My Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings available.</p>
      ) : (
        bookings.map((booking, index) => (
          <div key={index} className="booking-card">
            <p>
              <strong>Room:</strong> {booking.room?.name || "N/A"}
            </p>
            <p>
              <strong>Name:</strong> {booking.customerName || "N/A"}
            </p>
            <p>
              <strong>Email:</strong> {booking.customerEmail || "N/A"}
            </p>
            <p>
              <strong>Check-In:</strong> {booking.checkInDate || "N/A"}
            </p>
            <p>
              <strong>Check-Out:</strong> {booking.checkOutDate || "N/A"}
            </p>
            <p>
              <strong>Status:</strong>{" "}
              <span
                className={`status ${
                  booking.status === "APPROVED"
                    ? "status-approved"
                    : booking.status === "REJECTED"
                    ? "status-rejected"
                    : "status-pending"
                }`}
              >
                {booking.status || "PENDING"}
              </span>
            </p>
            <hr />
          </div>
        ))
      )}
    </div>
  );
};

export default MyBookings;
